import React from 'react';
import { Clock, Users, Utensils } from 'lucide-react';

const recipes = [
  {
    id: 1,
    title: "Mediterranean Quinoa Bowl",
    image: "https://www.eatingbirdfood.com/wp-content/uploads/2022/11/mediterranean-quinoa-bowl-hero.jpg",
    prepTime: "20 mins",
    servings: 2,
    difficulty: "Easy",
    description: "A healthy and filling quinoa bowl with fresh vegetables, feta cheese, and olive oil dressing."
  },
  {
    id: 2,
    title: "Grilled Chicken Avocado Wrap",
    image: "https://images.unsplash.com/photo-1626700051175-6818013e1d4f?auto=format&fit=crop&q=80&w=800",
    prepTime: "15 mins",
    servings: 1,
    difficulty: "Easy",
    description: "Tender grilled chicken with creamy avocado, fresh vegetables, and chipotle mayo in a wrap."
  },
  {
    id: 3,
    title: "Asian Noodle Salad",
    image: "data:image/jpeg;base64,",
    prepTime: "25 mins",
    servings: 4,
    difficulty: "Medium",
    description: "Cold noodles tossed with crunchy vegetables and a sesame-ginger dressing."
  }
];

function RecipeCard({ recipe }) {
  return (
    <div className="bg-gradient-to-b from-orange-50 to-white rounded-lg shadow-lg overflow-hidden transition-transform hover:scale-[1.02]">
      <img 
        src={recipe.image} 
        alt={recipe.title} 
        className="w-full h-48 object-cover"
      />
      <div className="p-6">
        <h3 className="text-xl font-bold mb-2">{recipe.title}</h3>
        <p className="text-gray-600 mb-4">{recipe.description}</p>
        <div className="flex items-center gap-6 text-sm text-gray-500">
          <div className="flex items-center gap-1">
            <Clock size={16} />
            <span>{recipe.prepTime}</span>
          </div>
          <div className="flex items-center gap-1">
            <Users size={16} />
            <span>{recipe.servings} servings</span>
          </div>
          <div className="flex items-center gap-1">
            <Utensils size={16} />
            <span>{recipe.difficulty}</span>
          </div>
        </div>
      </div>
    </div>
  );
}

function Lunch() {
  return (
    <div className="min-h-screen bg-gray-50 bg-gradient-to-b from-orange-50 to-white">
      <main className="max-w-7xl mx-auto px-4 py-8 sm:px-6 lg:px-8 mt-16">
        {/* Add heading above the grid */}
        <h1 className="text-3xl font-bold text-center mb-8">Lunch Recipes</h1>
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8">
          {recipes.map(recipe => (
            <RecipeCard key={recipe.id} recipe={recipe} />
          ))}
        </div>
      </main>
    </div>
  );
}



export default Lunch;